import React, { Component } from "react";
import Producto from "./Producto";

class CarritoDeCompras extends Component {
  state = {
    productos: [
      {
        id: 1,
        nombre: "Rayuela",
        autor: "Julio Cortazar",
        precio: 600,
        cantidad: 10,
      },
      {
        id: 2,
        nombre: "El Aleph",
        autor: "Jorge L. Borges",
        precio: 450,
        cantidad: 8,
      },
      {
        id: 3,
        nombre: "El Sur",
        autor: "Jorge L. Borges",
        precio: 250,
        cantidad: 12,
      },
      {
        id: 4,
        nombre: "El Túnel",
        autor: "Ernesto Sábato",
        precio: 400,
        cantidad: 6,
      },
      {
        id: 5,
        nombre: "Antes del Fin",
        autor: "Ernesto Sábato",
        precio: 350,
        cantidad: 7,
      },
      {
        id: 6,
        nombre: "El reino del revés",
        autor: "Maria E. Walsh",
        precio: 300,
        cantidad: 5,
      },
      {
        id: 7,
        nombre: "Tutú Marambá",
        autor: "Maria E. Walsh",
        precio: 200,
        cantidad: 3,
      },
      {
        id: 8,
        nombre: "Bestiario",
        autor: "Julio Cortazar",
        precio: 350,
        cantidad: 4,
      },
    ],
  };

  render() {
    return (
      <div>
        <h4>Carrito</h4>
        <div>
          {this.state.productos.map((prod) => {
            return <Producto key={prod.id} />;
          })}
        </div>
      </div>
    );
  }
}

export default CarritoDeCompras;
