import React, { Component } from "react";

class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      usuario: "admin",
      contraseña: "admin",
      mensaje: "",
    };
  }

  render() {
    return (
      <div className="col-lg-9">
        <h4 className="m-1 p-2 border-bottom">Login</h4>
        {/* Casilla de Usuario */}
        <div className="form-group form-row">
          <label className="col-lg-3">Usuario:</label>
          <input
            type="text"
            className="form-control"
            value={this.state.usuario}
            onChange={(event) => {
              this.setState({ usuario: event.target.value });
            }}
          />
        </div>

        {/* Casilla de Contraseña */}
        <div className="form-group form-row">
          <label className="col-lg-3">Contraseña:</label>
          <input
            type="password"
            className="form-control"
            value={this.state.contraseña}
            onChange={(event) => {
              this.setState({ contraseña: event.target.value });
            }}
          />
        </div>

        {/* Botón de Login */}
        <div className="text-right">
          {this.state.mensaje}
          <button className="btn btn-primary m-2" onClick={this.onLoginClick}>
            Ingresar
          </button>
        </div>
      </div>
    );
  }

  onLoginClick = () => {
    if (this.state.usuario === "admin" && this.state.contraseña === "admin") {
      this.setState({
        mensaje: <span className="text-success">Login exitoso</span>,
      });
    } else {
      this.setState({
        mensaje: <span className="text-danger">Login inválido</span>,
      });
    }
  };
}

export default Login;
