import React, { Component } from "react";

class MainContent extends Component {
  state = {
    pageTitle: "Clientes:",
    customersCount: 5,
    clientes: [
      { id: 1, nombre: "Lionel", numero_de_telefono: "1234-5678" },
      { id: 2, nombre: "Rodrigo", numero_de_telefono: "1234-5678" },
      { id: 3, nombre: "Gonzalo", numero_de_telefono: "1234-5678" },
      { id: 4, nombre: "Angel", numero_de_telefono: null },
      { id: 5, nombre: "Leandro", numero_de_telefono: "1234-5678" },
    ],
  };

  render() {
    return (
      <div>
        <h4 className="border-bottom m-1 p-1">
          {this.state.pageTitle}
          <span className="m-1 p-1">{this.state.customersCount}</span>
          <button className="btn btn-info" onClick={this.onRefreshClick}>
            Refresh
          </button>
        </h4>

        <table className="table">
          <thead>
            <tr>
              <th>#</th>
              <th>Clientes</th>
              <th>Numero de Tel√©fono</th>
            </tr>
          </thead>
          <tbody>
            {this.state.clientes.map((cliente) => {
              return (
                <tr key={cliente.id}>
                  <td>{cliente.id}</td>
                  <td>{cliente.nombre}</td>
                  <td>
                    {cliente.numero_de_telefono == null ? (
                      <div className="bg-warning p-2">No disponible</div>
                    ) : (
                      cliente.numero_de_telefono
                    )}
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    );
  }

  // Se Ejecuta cuando el usuario cliquea en Refresh
  onRefreshClick = () => {
    this.setState({
      customersCount: 7,
    });
  };
}

export default MainContent;
