import React, { Component } from "react";
import Producto from "./Producto";

class CarritoDeCompras extends Component {
  state = {
    productos: [
      {
        id: 1,
        nombre: "Rayuela",
        autor: "Julio Cortazar",
        precio: 600,
        cantidad: 0,
      },
      {
        id: 2,
        nombre: "El Aleph",
        autor: "Jorge L. Borges",
        precio: 450,
        cantidad: 0,
      },
      {
        id: 3,
        nombre: "El Sur",
        autor: "Jorge L. Borges",
        precio: 250,
        cantidad: 0,
      },
      {
        id: 4,
        nombre: "El Túnel",
        autor: "Ernesto Sábato",
        precio: 400,
        cantidad: 0,
      },
      {
        id: 5,
        nombre: "Antes del Fin",
        autor: "Ernesto Sábato",
        precio: 350,
        cantidad: 0,
      },
      {
        id: 6,
        nombre: "El reino del revés",
        autor: "Maria E. Walsh",
        precio: 300,
        cantidad: 0,
      },
      {
        id: 7,
        nombre: "Tutú Marambá",
        autor: "Maria E. Walsh",
        precio: 200,
        cantidad: 0,
      },
      {
        id: 8,
        nombre: "Bestiario",
        autor: "Julio Cortazar",
        precio: 350,
        cantidad: 0,
      },
    ],
  };

  render() {
    return (
      <div className="container-fluid">
        <h4>Carrito</h4>
        <div className="row">
          {this.state.productos.map((prod, indice) => {
            return (
              <Producto
                key={prod.id}
                producto={prod}
                indice={indice}
                onIncrement={this.handleIncrement}
                onDecrement={this.handleDecrement}
              >
                <button className="btn btn-primary">Comprar</button>
              </Producto>
            );
          })}
        </div>
      </div>
    );
  }

  handleIncrement = (indice) => {
    var productsArray = this.state.productos;
    productsArray[indice].cantidad += 1;
    this.setState({ productos: productsArray });
  };

  handleDecrement = () => {};
}

export default CarritoDeCompras;
