import React, { Component } from "react";
import Producto from "./Producto";

class CarritoDeCompras extends Component {
  constructor() {
    // llamar al constructor de la superclase
    super();
    // Inicializar el estado
    this.state = {
      productos: [
        {
          id: 1,
          nombre: "Rayuela",
          autor: "Julio Cortazar",
          precio: 600,
          cantidad: 0,
        },
        {
          id: 2,
          nombre: "El Aleph",
          autor: "Jorge L. Borges",
          precio: 450,
          cantidad: 0,
        },
        {
          id: 3,
          nombre: "El Sur",
          autor: "Jorge L. Borges",
          precio: 250,
          cantidad: 0,
        },
        {
          id: 4,
          nombre: "El Túnel",
          autor: "Ernesto Sábato",
          precio: 400,
          cantidad: 0,
        },
        {
          id: 5,
          nombre: "Antes del Fin",
          autor: "Ernesto Sábato",
          precio: 350,
          cantidad: 0,
        },
        {
          id: 6,
          nombre: "El reino del revés",
          autor: "Maria E. Walsh",
          precio: 300,
          cantidad: 0,
        },
        {
          id: 7,
          nombre: "Tutú Marambá",
          autor: "Maria E. Walsh",
          precio: 200,
          cantidad: 0,
        },
        {
          id: 8,
          nombre: "Bestiario",
          autor: "Julio Cortazar",
          precio: 350,
          cantidad: 0,
        },
      ],
    };
  }

  render() {
    return (
      <div className="container-fluid">
        <h4>Carrito</h4>
        <div className="row">
          {this.state.productos.map((prod) => {
            return (
              <Producto
                key={prod.id}
                producto={prod}
                onIncrement={this.handleIncrement}
                onDecrement={this.handleDecrement}
                onDelete={this.handleDelete}
              >
                <button className="btn btn-primary">Comprar</button>
              </Producto>
            );
          })}
        </div>
      </div>
    );
  }

  handleIncrement = (producto) => {
    let nuevosProductos = [...this.state.productos];
    let indice = nuevosProductos.indexOf(producto);
    nuevosProductos[indice].cantidad++;
    this.setState({ productos: nuevosProductos });
  };

  handleDecrement = (producto) => {
    let nuevosProductos = [...this.state.productos];
    let indice = nuevosProductos.indexOf(producto);
    let cantidadActual = nuevosProductos[indice].cantidad;
    cantidadActual--;
    nuevosProductos[indice].cantidad = Math.max(0, cantidadActual);
    this.setState({ productos: nuevosProductos });
  };

  handleDelete = (producto) => {
    let nuevosProductos = [...this.state.productos];
    let indice = nuevosProductos.indexOf(producto);

    if (
      window.confirm("Está seguro que desea quitar el elemento del carrito?")
    ) {
      nuevosProductos.splice(indice, 1);
      this.setState({ productos: nuevosProductos });
    }
  };
}

export default CarritoDeCompras;
