import React, { Component } from "react";
import Producto from "./ProductoEnCatálogo";
import user_info from "./user_info";

class Historial extends Component {
  constructor(props) {
    // llamar al constructor de la superclase
    super(props);
    // Inicializar el estado
    this.state = {
      items: [],
    };
  }

  render() {
    return (
      <div>
        <h4>Historial de Compras</h4>
        {this.state.items.map((item) => (
          <div>{item}</div>
        ))}
      </div>
    );
  }

  componentDidMount = async () => {
    /* Obtener el Catálogo de Libros */
    var response = await fetch(
      `http://localhost:8080/createCart?clientId=${this.state.usuario}&password=${this.state.contraseña}`,
      {
        method: "GET",
      }
    );

    /* Actualizar la lista de productos */
    var historial = await response.json();
    this.setState({ items: historial });
  };
}

export default Historial;
