import React, { Component } from "react";

class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      usuario: "admin",
      contraseña: "admin",
      mensaje: "",
    };
  }

  render() {
    return (
      <div className="col-lg-9">
        <h4 className="m-1 p-2 border-bottom">Login</h4>
        {/* Casilla de Usuario */}
        <div className="form-group form-row">
          <label className="col-lg-3">Usuario:</label>
          <input
            type="text"
            className="form-control"
            value={this.state.usuario}
            onChange={(event) => {
              this.setState({ usuario: event.target.value });
            }}
          />
        </div>

        {/* Casilla de Contraseña */}
        <div className="form-group form-row">
          <label className="col-lg-3">Contraseña:</label>
          <input
            type="text"
            className="form-control"
            value={this.state.contraseña}
            onChange={(event) => {
              this.setState({ contraseña: event.target.value });
            }}
          />
        </div>

        <div className="text-right">
          <button className="btn btn-primary" onClick={this.onLoginClick}>
            Ingresar
          </button>
        </div>
      </div>
    );
  }

  onLoginClick = () => {
    if (this.state.usuario == "admin" && this.state.password == "admin") {
      this.state.mensaje = <span className="text-success">Login exitoso</span>;
    } else {
      this.state.mensaje = <span className="text-failure">Login inválido</span>;
    }
  };
}

export default Login;
