import React, { Component } from "react";

class MainContent extends Component {
  state = {
    pageTitle: "Clientes:",
    customersCount: 5,
    clientes: [
      { id: 1, nombre: "Lionel", numero_de_telefono: "1234-5678" },
      { id: 2, nombre: "Rodrigo", numero_de_telefono: "1234-5678" },
      { id: 3, nombre: "Gonzalo", numero_de_telefono: "1234-5678" },
      { id: 4, nombre: "Angel", numero_de_telefono: "1234-5678" },
      { id: 5, nombre: "Leandro", numero_de_telefono: "1234-5678" },
    ],
  };

  render() {
    return (
      <div>
        <h4 className="border-bottom m-1 p-1">
          {this.state.pageTitle}
          <span className="m-1 p-1">{this.state.customersCount}</span>
          <button className="btn btn-info" onClick={this.onRefreshClick}>
            Refresh
          </button>
        </h4>

        <table className></table>
      </div>
    );
  }

  // Se Ejecuta cuando el usuario cliquea en Refresh
  onRefreshClick = () => {
    this.setState({
      customersCount: 7,
    });
  };
}

export default MainContent;
